package com.java.practice03;

public class Ex03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


	int x, y;
	for( x=0; x<4; x++) { //3. 밑으로 돌아감
		for(y=2; y>=0; y--) {
			System.out.print("*"); //1.  2부터 시작 -> 별 출력 ->  3번 반복
			}
		System.out.println(""); // 2. 3번 반복이 되면, 공백 띠워줌
		}
	
	
	
	}

}
