package com.java.practice03;

public class Ex05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1;
		boolean flag = true;
		
		while(flag) {
			if(i>=5) {
				flag=false;
			}
			System.out.println(i); // i값 출력 -> i+1값을 4회까지 반복 -> 1 2 3 4 5 세로로 출력!
			i=i+1; 
		}
	
	
	
	
	
	
	
	}

}
